def decorator(decorator):
	def new(func):
		def new2(*args, **kw):
			return decorator(func, *args, **kw)
		new2.__name__ = func.__name__
		new2.__doc__ = func.__doc__
		new2.__dict__.update(func.__dict__)
		return new2
	return new

@decorator
def memoised(func, *args, **kw):
	if not hasattr(func, '_cache'):
		func._cache = dict()
	d = func._cache
	if args in d: return d[args]
	result = func(*args, **kw)
	d[args] = result
	return result

@decorator
def logged(func, *args, **kw):
	import sys
	sys.stderr.write("Calling %s with %r"%(func.__name__, args))
	return func(*args, **kw)

def subscriptable(func):
	class MethodReplacement:
		def __init__(self):
			self.func = func
			self.__name__ = func.__name__
			self.__doc__ = func.__doc__
			self.__dict__.update(func.__dict__)

		def __call__(self, *args):
			return self.func(*args)

		def __getitem__(self, *args):
			return self.func(*args)
	return MethodReplacement()
